mapper.insert:

    	INSERT INTO company
		(
			  name
			, contact
			, email
			, address_id
			, description
			, status
			, create_by
			, create_date
		)
		VALUES (
		  #{name}
		, #{contact}
		, #{email}
		, #{addressID}
		, COALESCE(#{description}, '' )
		, #{status}
		, #{userID}
		, to_char(NOW(), 'YYYYMMDD')
		)

mapper.update:

UPDATE company
SET
	  name 			= #{name}
	, contact		= #{contact}
	, email			= #{email}
	, address_id	= #{addressID}
	, description 	= COALESCE(#{description},'')
	, status		= #{status}
	, modify_by		= #{userID}
	, modify_date	= to_char(NOW(), 'YYYYMMDD')
WHERE id 			= #{id}


		update company
        set
             status  		= #{status}
            , modify_by   	= #{userID}
            , modify_date	= to_char(NOW(), 'YYYYMMDD')
        where id   			= #{id}
